mapper.select:
select
  user_selected_room, building_id, uid, room_id
from user_selected_room


        and user_selected_room = #{userSelectedRoom}


        and building_id = #{buildingId}


        and uid = #{uid}


        and room_id = #{roomId}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from user_selected_room


        and user_selected_room = #{userSelectedRoom}


        and building_id = #{buildingId}


        and uid = #{uid}


        and room_id = #{roomId}
